import Layout from '../../components/layout'
import unfetch from 'isomorphic-unfetch'
import Link from 'next/link'
function CharacterDetail({character}) {
  return (
    <Layout>
      <h1>Character Detail</h1>
      <div>{character.name}</div>
    </Layout>
  )
}
export async function getStaticPaths() {
  const data = await unfetch('https://rickandmortyapi.com/api/character/'+character.id)
  const characters = await data.json()

  return {
    paths: characters.results.map(character =>{
      return {params: { id: character.id }}
    }),
      //{ params: { id: '1' } },

    fallback: false // See the "fallback" section below
  };
}

export async function getStaticProps(params) {
  //data fetch
  const data = await unfetch('https://rickandmortyapi.com/api/character/'+ params.id)
  const character = await data.json()

  return {
    props: {
      character
    }
  }
}

export default CharacterDetail
